config.label:
{{tbl_users.processedTableData.map((currentRow, currentRowIndex) => {
        let primaryColumnData = [];

        if (tbl_users.primaryColumns.Role.sourceData[currentRowIndex].length) {
          primaryColumnData = tbl_users.primaryColumns.Role.sourceData[currentRowIndex];
        } else if (tbl_users.primaryColumns.Role.sourceData.length) {
          primaryColumnData = tbl_users.primaryColumns.Role.sourceData;
        }

        return primaryColumnData.map((currentItem, currentIndex) => currentItem.Name);});}}

jz3gpe827y.data:
{{ 
 Object.entries(
  GetAllUsers.data.reduce((acc, user) => {
    const date = new Date(user.CreatedAt);
    const monthYear = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`; // Format: YYYY-MM
    acc[monthYear] = (acc[monthYear] || 0) + 1;
    return acc;
  }, {})
).map(([monthYear, count]) => ({ x: monthYear, y: count }));

}}

