function (rawData, groupBy, graphType) {
  function formatDateKey(dateStr) {
    if (!dateStr) return null;
    const d = new Date(dateStr);
    if (isNaN(d.getTime())) {
      console.warn(`Invalid date: ${dateStr}`);
      return null;
    }
    if (groupBy === "day") {
      return d.toISOString().slice(0, 10);
    } else if (groupBy === "month") {
      return d.toISOString().slice(0, 7);
    } else if (groupBy === "year") {
      return d.getFullYear().toString();
    }
    return d.toISOString().slice(0, 10);
  }
  const grouped = {};
  rawData.forEach(item => {
    if (!item.ChangedAt) {
      console.warn("Skipping item with missing ChangedAt:", item);
      return;
    }
    const key = formatDateKey(item.ChangedAt);
    if (!key) {
      console.warn("Skipping item with invalid date:", item.ChangedAt);
      return;
    }
    if (!grouped[key]) {
      grouped[key] = {
        time: key,
        NewValue_1: null,
        NewValue_2: null,
        ChangedAt: null
      };
    }
    const currentDate = new Date(item.ChangedAt);
    const storedDate = grouped[key].ChangedAt ? new Date(grouped[key].ChangedAt) : null;
    if (!storedDate || currentDate > storedDate) {
      grouped[key].NewValue_1 = parseFloat(item.NewValue_1) || 0;
      grouped[key].NewValue_2 = parseFloat(item.NewValue_2) || 0;
      grouped[key].ChangedAt = item.ChangedAt;
    }
  });
  if (Object.keys(grouped).length === 0) {
    console.warn("No valid data after filtering");
    return {
      series: []
    };
  }
  const sortedKeys = Object.keys(grouped).sort();
  const product1 = [];
  const product2 = [];
  sortedKeys.forEach(key => {
    product1.push(grouped[key].NewValue_1 ?? 0);
    product2.push(grouped[key].NewValue_2 ?? 0);
  });
  function formatLabel(key) {
    if (groupBy === "day") return key;
    if (groupBy === "month") {
      const [year, month] = key.split("-");
      const monthNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
      return monthNames[parseInt(month) - 1] + " " + year;
    }
    if (groupBy === "year") return key;
    return key;
  }
  const xLabels = sortedKeys.map(formatLabel);
  if (graphType === "appsmith") {
    return {
      series: [{
        name: "Product 1",
        data: product1.map((v, i) => ({
          x: xLabels[i],
          y: v
        }))
      }, {
        name: "Product 2",
        data: product2.map((v, i) => ({
          x: xLabels[i],
          y: v
        }))
      }]
    };
  }
  if (graphType === "echart") {
    return {
      xAxis: {
        type: "category",
        data: xLabels
      },
      series: [{
        name: "Product 1",
        type: "line",
        data: product1
      }, {
        name: "Product 2",
        type: "line",
        data: product2
      }],
      legend: {
        data: ["Product 1", "Product 2"]
      },
      tooltip: {
        trigger: "axis"
      }
    };
  }
  if (graphType === "fusion") {
    return {
      type: "mscolumn3d",
      width: "100%",
      height: "360",
      dataFormat: "json",
      dataSource: {
        chart: {
          caption: "Sales Comparison",
          subCaption: `Grouped by ${groupBy}`,
          xAxisName: groupBy.charAt(0).toUpperCase() + groupBy.slice(1),
          yAxisName: "Value",
          theme: "fusion",
          chartBottomMargin: "40",
          labelDisplay: "auto",
          slantLabels: "0",
          legendPosition: "bottom",
          legendAlignment: "left",
          legendBorderAlpha: "0",
          legendShadow: "0",
          legendAllowDrag: "0"
        },
        categories: [{
          category: xLabels.map(label => ({
            label
          }))
        }],
        dataset: [{
          seriesname: "Product 1",
          data: product1.map(value => ({
            value: value.toString()
          }))
        }, {
          seriesname: "Product 2",
          data: product2.map(value => ({
            value: value.toString()
          }))
        }]
      }
    };
  }
  return rawData;
}

