export default {
	// Login function that handles user authentication
	async SignIn() {
		if (!(inp_email.isValid & inp_password.isValid)) {showAlert("Preencha os campos corretamente", "error"); return;};

		try {
			//Store the user details
			const getUserDetailsresponse = await GetUserDetails.run();
			if (getUserDetailsresponse && getUserDetailsresponse.Name)
			{ 
				storeValue('userId', getUserDetailsresponse.Id);
				storeValue('userName', getUserDetailsresponse.Name);
				storeValue('userEmail', getUserDetailsresponse.Email);
				storeValue('userUsername', getUserDetailsresponse.Username);
				storeValue('userRole', getUserDetailsresponse.Role);
				storeValue('userGroup', getUserDetailsresponse.Group);
				storeValue('userTel', getUserDetailsresponse.Tel);
				storeValue('userPhoto', getUserDetailsresponse.Photo);

				await GetAllRoles.run();
				storeValue('userRoleId', GetAllRoles.data.find(role => role.Name === getUserDetailsresponse.Role)?.Id);

			}

			await Auth_PasswordCheck.getSaltAndHash(inp_password.text, false);
			const loginResponse = await Login_GetAuthToken.run();

			if (loginResponse && loginResponse.Token) {
				// Success - store token and return
				storeValue('authToken', loginResponse.Token);


				showAlert("Autenticado com Sucesso", "success");
				navigateTo("Main Page");

			} else {
				// Handle login failure
				if (Login_GetAuthToken.responseMeta.statusCode.match("401 UNAUTHORIZED")) {
					showAlert("Erro: Password Incorreta", "warning");
				}
			}
		} catch (error) {
			try{
			// Handle login failure
			if (Login_GetAuthToken.responseMeta.statusCode.match("401 UNAUTHORIZED")) {
				showAlert("Erro: Password Incorreta", "warning");
			}
			else {
				showAlert("Erro no Login: " + (error.message || "Erro desconhecido"), "error");
			}
				}
			catch {showAlert("Erro no Login: " + (error.message || "Erro desconhecido"), "error");};
		}
	}
}

