export default {
  checkAuthToken: async () => {
    try {
      const response = await CheckToken.run();

      // Defensive check for responseMeta statusCode
      const statusCode = CheckToken.responseMeta?.statusCode || "";

      if (!appsmith.store.authToken || statusCode.startsWith("403")) {
        // Redirect to login page
        navigateTo("Login");
      }

      // Optionally return true or something if you want
      return true;
    } catch (error) {
      // Handle error gracefully
      console.error("Error checking auth token:", error);
      // Maybe redirect to login or just return false
      navigateTo("Login");
      return false;
    }
  },
};

