export default {
    FormatGraphData(rawData, groupBy, graphType) {
        // Helper: validate and format date keys according to groupBy
        function formatDateKey(dateStr) {
            // Validate date first
            if (!dateStr) return null;

            const d = new Date(dateStr);
            // Check if date is valid
            if (isNaN(d.getTime())) {
                console.warn(`Invalid date: ${dateStr}`);
                return null;
            }

            if (groupBy === "day") {
                return d.toISOString().slice(0, 10); // yyyy-mm-dd
            } else if (groupBy === "month") {
                return d.toISOString().slice(0, 7); // yyyy-mm
            } else if (groupBy === "year") {
                return d.getFullYear().toString(); // yyyy
            }
            return d.toISOString().slice(0, 10); // default to day format
        }

        // Filter out invalid data and aggregate
        const grouped = {};
        rawData.forEach(item => {
            // Skip items with invalid or missing dates
            if (!item.ChangedAt) {
                console.warn("Skipping item with missing ChangedAt:", item);
                return;
            }

            const key = formatDateKey(item.ChangedAt);
            if (!key) {
                console.warn("Skipping item with invalid date:", item.ChangedAt);
                return;
            }

            if (!grouped[key]) {
                grouped[key] = { time: key, NewValue_1: null, NewValue_2: null, ChangedAt: null };
            }

            // Only update if this ChangedAt is later than stored (to keep latest in bucket)
            const currentDate = new Date(item.ChangedAt);
            const storedDate = grouped[key].ChangedAt ? new Date(grouped[key].ChangedAt) : null;

            if (!storedDate || currentDate > storedDate) {
                grouped[key].NewValue_1 = parseFloat(item.NewValue_1) || 0;
                grouped[key].NewValue_2 = parseFloat(item.NewValue_2) || 0;
                grouped[key].ChangedAt = item.ChangedAt;
            }
        });

        // Check if we have any valid data
        if (Object.keys(grouped).length === 0) {
            console.warn("No valid data after filtering");
            return { series: [] }; // Return empty series for appsmith
        }

        // Sort keys ascending (time)
        const sortedKeys = Object.keys(grouped).sort();

        // Prepare arrays for each product series
        const product1 = [];
        const product2 = [];
        sortedKeys.forEach(key => {
            product1.push(grouped[key].NewValue_1 ?? 0);
            product2.push(grouped[key].NewValue_2 ?? 0);
        });

        // Format x axis labels nicely for FusionCharts or ECharts
        function formatLabel(key) {
            if (groupBy === "day") return key; // yyyy-mm-dd
            if (groupBy === "month") {
                const [year, month] = key.split("-");
                const monthNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
                return monthNames[parseInt(month) - 1] + " " + year;
            }
            if (groupBy === "year") return key;
            return key;
        }

        const xLabels = sortedKeys.map(formatLabel);

        // Format output for each graph type
        if (graphType === "appsmith") {
            // Appsmith default line or bar chart
            return {
                series: [
                    {
                        name: "Product 1",
                        data: product1.map((v, i) => ({ x: xLabels[i], y: v })),
                    },
                    {
                        name: "Product 2",
                        data: product2.map((v, i) => ({ x: xLabels[i], y: v })),
                    },
                ],
            };
        }

        if (graphType === "echart") {
            return {
                xAxis: {
                    type: "category",
                    data: xLabels,
                },
                series: [
                    {
                        name: "Product 1",
                        type: "line",
                        data: product1,
                    },
                    {
                        name: "Product 2",
                        type: "line",
                        data: product2,
                    },
                ],
                legend: {
                    data: ["Product 1", "Product 2"],
                },
                tooltip: {
                    trigger: "axis",
                },
            };
        }

        if (graphType === "fusion") {
            // FusionCharts MSColumn3D format
            return {
                type: "mscolumn3d",
                width: "100%",
                height: "360",
                dataFormat: "json",
                dataSource: {
                    chart: {
                        caption: "Sales Comparison",
                        subCaption: `Grouped by ${groupBy}`,
                        xAxisName: groupBy.charAt(0).toUpperCase() + groupBy.slice(1),
                        yAxisName: "Value",
                        theme: "fusion",
                        chartBottomMargin: "40",
                        labelDisplay: "auto",
                        slantLabels: "0",
                        legendPosition: "bottom",
                        legendAlignment: "left",
                        legendBorderAlpha: "0",
                        legendShadow: "0",
                        legendAllowDrag: "0",
                    },
                    categories: [
                        {
                            category: xLabels.map(label => ({ label })),
                        },
                    ],
                    dataset: [
                        {
                            seriesname: "Product 1",
                            data: product1.map(value => ({ value: value.toString() })),
                        },
                        {
                            seriesname: "Product 2",
                            data: product2.map(value => ({ value: value.toString() })),
                        },
                    ],
                },
            };
        }

        // Default fallback (raw)
        return rawData;
    },

    // Debug helper to check your data
    debugData(rawData) {
        console.log("Raw data sample:", rawData.slice(0, 3));
        const invalidDates = rawData.filter(item => !item.ChangedAt || isNaN(new Date(item.ChangedAt).getTime()));
        console.log("Invalid dates found:", invalidDates.length);
        if (invalidDates.length > 0) {
            console.log("Sample invalid dates:", invalidDates.slice(0, 5));
        }
        return `Total items: ${rawData.length}, Invalid dates: ${invalidDates.length}`;
    }
}

