			if (groupBy === "day") {
				return d.toISOString().slice(0, 10); // yyyy-mm-dd
			} else if (groupBy === "month") {
				return d.toISOString().slice(0, 7); // yyyy-mm
			} else if (groupBy === "year") {
				return d.getFullYear().toString(); // yyyy
			}
			return d.toISOString();
		}

		// Aggregate data by dateKey, keep latest NewValue_1 and NewValue_2 per bucket
		const grouped = {};
		rawData.forEach(item => {
			const key = formatDateKey(item.ChangedAt);
			if (!grouped[key]) {
				grouped[key] = { time: key, NewValue_1: null, NewValue_2: null, ChangedAt: null };
			}
			// Only update if this ChangedAt is later than stored (to keep latest in bucket)
			if (!grouped[key].ChangedAt || new Date(item.ChangedAt) > new Date(grouped[key].ChangedAt)) {
				grouped[key].NewValue_1 = parseFloat(item.NewValue_1);
				grouped[key].NewValue_2 = parseFloat(item.NewValue_2);
				grouped[key].ChangedAt = item.ChangedAt;
			}
		});

		// Sort keys ascending (time)
		const sortedKeys = Object.keys(grouped).sort();

