jsonPathKeys:
async departmentId => {
  try {
    const response = await fetch(`https://modsi-api-ffhhfgecfdehhscv.spaincentral-01.azurewebsites.net/api/departments/${departmentId}/detail?code=z4tKbNFdaaXzHZ4ayn9pRQokNWYgRkbVkCjOxTxP-8ChAzFuMigGCw==`);
    if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
    const departmentData = await response.json();
    const formattedKPIs = departmentData.KPIs.map(kpi => ({
      "name": `${kpi.Name}`,
      "code": `${kpi.Id}`
    }));
    console.log(`Formatted KPIs for department ${departmentId}:`, formattedKPIs);
    return formattedKPIs;
  } catch (error) {
    console.error(`Error fetching KPIs for department ${departmentId}:`, error);
    return [];
  }
}

