actionConfiguration.body:
SELECT date_range.date, COALESCE(counts.num_entries, 0) AS count
FROM generate_series(NOW() - INTERVAL '9 days', NOW(), INTERVAL '1 day') date_range(date)
LEFT JOIN (
  SELECT DATE(created) AS date, COUNT(*) AS num_entries
  FROM kyc 
  WHERE status = {{this.params.status || 'PENDING'}} 
  AND created >= NOW() - INTERVAL '9 days'
  GROUP BY DATE(created)
) counts ON counts.date = date_range.date::date
ORDER BY date_range.date;

SELECT date_range.date, COALESCE(counts.num_entries, 0) AS count
FROM generate_series(NOW() - INTERVAL '9 days', NOW(), INTERVAL '1 day') date_range(date)
LEFT JOIN (
  SELECT DATE(created) AS date, COUNT(*) AS num_entries
  FROM kyc 
  WHERE status = {{this.params.status || 'PENDING'}} 
  AND created >= NOW() - INTERVAL '9 days'
  GROUP BY DATE(created)
) counts ON counts.date = date_range.date::date
ORDER BY date_range.date;

async () => {
  const data = await getAllKyc.run();
  const today = new Date();
  const last12Months = new Date(today.getFullYear(), today.getMonth() - 11, 1);
  const counts = {};
  data.filter(obj => obj.status === 'VERIFIED').forEach(obj => {
    const createdDate = new Date(obj.created);
    if (createdDate >= last12Months && createdDate <= today) {
      const monthYear = createdDate.toLocaleDateString('en-US', {
        month: 'short',
        year: 'numeric'
      });
      counts[monthYear] = (counts[monthYear] || 0) + 1;
    }
  });
  const result = [];
  let currentMonth = new Date(last12Months);
  while (currentMonth <= today) {
    const monthYear = currentMonth.toLocaleDateString('en-US', {
      month: 'short',
      year: 'numeric'
    });
    const count = counts[monthYear] || 0;
    result.push({
      monthYear,
      count
    });
    currentMonth.setMonth(currentMonth.getMonth() + 1);
  }
  return result;
}

