async action => {
  const totalDoc = await getKycDocument.run();
  const totalDocLength = totalDoc.length;
  const prevDocIndex = appsmith.store.docIndex || 0;
  if (action === 'INCREASE') {
    if (prevDocIndex == parseInt(totalDocLength) - 1) {
      return;
    }
    storeValue('docIndex', prevDocIndex + 1);
  } else {
    if (prevDocIndex === 0) {
      return;
    }
    storeValue('docIndex', prevDocIndex - 1);
  }
}

