actionConfiguration.body:
() => {
  const counts = {};
  getAllKyc.data.forEach(obj => {
    const status = obj.status;
    if (!counts[status]) {
      counts[status] = 1;
    } else {
      counts[status]++;
    }
  });
  const result = [];
  for (const [status, count] of Object.entries(counts)) {
    result.push({
      x: status,
      y: count
    });
  }
  return result;
}

