export default {
	btn_confirmonClick: async () => {
		try {
			const email = inp_email.text;
			const role = change_role.selectedOptionValue;
			const newPassword = change_pass.text || "default123";
			const isAdmin = RadioGroup2.selectedOptionValue.isSwitchedOn;
			const group = change_group.selectedOptionValue;

			const apiUrl = `https://modsi-api-ffhhfgecfdehhscv.spaincentral-01.azurewebsites.net/api/User/UpdateByAdmin/${email}?code=z4tKbNFdaaXzHZ4ayn9pRQokNWYgRkbVkCjOxTxP-8ChAzFuMigGCw==`;

			// Prepare the request body
			let requestBody = {
				Role: role,
				Password: newPassword
			};

			// Set group based on conditions
			if (isAdmin) {
				requestBody.Group = 'Admin'; 
			} else if (group && group.trim() !== "") {
				requestBody.Group = group; 
			}

			const token = appsmith.store.authToken;
			const response = await fetch(apiUrl, {
				method: "PUT",
				headers: {
					"Content-Type": "application/json",
					"Authorization": `Bearer ${token}`
				},
				body: JSON.stringify(requestBody)
			});

			if (!response.ok) {
				const error = await response.text();
				console.error("API Error:", error);
				throw new Error("Failed to update user");
			}

			const result = await response.json();
			showAlert("User updated successfully!", "success");
			console.log("Update successful:", result);
			return result;

		} catch (error) {
			console.error("Update error:", error);
			showAlert(error.message || "An error occurred while updating user", "error");
			throw error;
		}
	}
}

