export default {
  getDepartmentKPIs: async (departmentId) => {
    try {
      // Fetch department details
      const response = await fetch(
        `https://modsi-api-ffhhfgecfdehhscv.spaincentral-01.azurewebsites.net/api/departments/${departmentId}/detail?code=z4tKbNFdaaXzHZ4ayn9pRQokNWYgRkbVkCjOxTxP-8ChAzFuMigGCw==`
      );

      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);

      const departmentData = await response.json();

      // Format KPIs with explicit string values
      const formattedKPIs = departmentData.KPIs.map(kpi => ({
        "name": `${kpi.Name}`,       // Ensures string output with quotes
        "code": `${kpi.Id}`          // Ensures string output with quotes
      }));

      console.log(`Formatted KPIs for department ${departmentId}:`, formattedKPIs);
      return formattedKPIs;

    } catch (error) {
      console.error(`Error fetching KPIs for department ${departmentId}:`, error);
      return [];
    }
  }
};

